<?php
namespace App\Entity\Checkout;


/**
 *@Entity(repositoryClass="App\Repository\Checkout\Transaction")
 * @Table(name="transactions")
 */

class Transaction
{
    /**
     *@Id @Column(type="integer", name="id")
     * @GeneratedValue 
     */
    private $_id;
    /** @Column(type="integer", name="order_id") */
    private $_orderId;
    /** @Column(type="string", name="gateway") */
    private $_gateway;
    /** @Column(type="string", name="status") */
    private $_status;
    /** @Column(type="string", name="action") */
    private $_action;
    /** @Column(type="string", name="notes") */
    private $_notes;
    /** @Column(type="array", name="raw_data") */
    private $_rawData;
    /** @Column(type="datetime", name="created") */
    private $_created;

    /**
     *  Creates a transaction object
     * @param integer $orderId - The order id the transaction is associated with
     * @param string $gateway - Name of the gateway creating the transaction
     * @param string $status - Status of the transaction
     * @param string $action - Transaction action being performed
     * @param string $notes - Additional information related to the transaction
     * @param mixed $rawData - The RAW data dump returned from the gateway provider
     */
    public function __construct($orderId, $gateway, $status, $action, $notes, $rawData)
    {
        $this->_orderId = $orderId;
        $this->_gateway = $gateway;
        $this->_status = $status;
        $this->_action = $action;
        $this->_notes = $notes;
        $this->_rawData = $rawData;
        $this->_created = new \DateTime;
    }

    /**
     * Returns the Order ID
     * @return integer
     */
    public function getOrderId()
    {
        return $this->_orderId;
    }

    /**
     * Sets the order ID
     * @param integer $id
     * @return Transaction
     */
    public function setOrderId($id)
    {
        $this->_orderId = $id;
        return $this;
    }

    /**
     * Returns the name of the gateway used for this transaction
     * @return string
     */
    public function getGateway()
    {
        return $this->_gateway;
    }

    /**
     * Sets the name of the gateway making the transaction
     * @param string $gateway
     * @return Transaction
     */
    public function setGateway($gateway)
    {
        $this->_gateway = $gateway;
        return $this;
    }

    /**
     * Returns the status of this transaction
     * @return string
     */
    public function getStatus()
    {
        return $this->_status;
    }

    /**
     * Sets the status of the transaction
     * @param string $status
     * @return Transaction
     */
    public function setStatus($status)
    {
        $this->_status = $status;
        return $this;
    }

    /**
     * Returns any additional information related to this transaction
     * @return string
     */
    public function getNotes()
    {
        return $this->_notes;
    }

    /**
     * Sets any additional information for this transaction
     * @param string $notes
     * @return Transaction
     */
    public function setNotes($notes)
    {
        $this->_notes = $notes;
        return $this;
    }

    /**
     * Returns the action being performed during this transaction
     * @return string
     */
    public function getAction()
    {
        return $this->_action;
    }

    /**
     * Sets the action being performed for this transaction
     * @param string $action
     * @return Transaction
     */
    public function setAction($action)
    {
        $this->_action = $action;
        return $this;
    }

    /**
     * Returns the RAW data generated for this transaction by the gateway
     * @return mixed
     */
    public function getRawData()
    {
        return $this->_rawData;
    }

    /**
     * Sets the RAW data generated by the gateway for this transaction
     * @param mixed $data
     * @return Transaction
     */
    public function setRawData($data)
    {
        $this->_rawData = $data;
        return $this;
    }
}